import React ,{Component} from 'react';
import web3 from '../web3.js';
import Storage from '../ethereum/build/contracts/Storage.json'; 
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
class App extends Component{
    static async getInitialProps(){
        try{
            const accounts = await web3.eth.getAccounts();
            const networkId = await web3.eth.net.getId();
            const StorageDeployedAddress = Storage.networks[networkId].address;
            const StorageInstance = await new web3.eth.Contract(Storage.abi, StorageDeployedAddress);
            const name = await StorageInstance.methods.name().call();
            return {accounts, StorageInstance,name}
        }catch(err){
            alert(
                `Failed to load web3, accounts, or contract. Check console for details.`,
              );
              console.log(error);
        }

    }

    useStyles = makeStyles((theme) => ({
        root: {
          flexGrow: 1,
        },
        title: {
          flexGrow: 1,
        },
      }));

    render(){
        const {accounts, StorageInstance,name}=this.props;
        return(
            <div className={classes.root}>
                <AppBar position="static">
                    <Toolbar>
                    <Typography variant="h6" className={classes.title}>
                        News
                    </Typography>
                    <Button color="inherit">{StorageInstance.options.address}</Button>
                    </Toolbar>
                </AppBar>
            </div>
        )
    }
}
export default App;
