import web3 from './web3.js';
const Storage =require('./ethereum/build/contracts/Storage.json');
console.log(Storage.abi)
let instance;
const init=async ()=>{
    const networkId = await web3.eth.net.getId();
    const deployedAddress =  Storage.networks[networkId].address;

    instance = new web3.eth.Contract(Storage.abi,deployedAddress);
}
init();
export default instance;
