import web3 from '../web3.js';
import Storage from '../ethereum/build/contracts/Storage.json';
class App extends Component{
    static async getInitialProps(){
        const accounts = await web3.eth.getAccounts();
        const networkId = await web3.eth.net.getId();
        const StorageDeployedAddress = Storage.networks[networkId].address;
        const StorageInstnace = await new web3.eth.Contract(Storage.abi, StorageDeployedAddress);
        console.log(StorageInstnace)
        return {accounts}
    }
    render(){
        console.log(this.props.accounts)
        return(
            <div>
                <h1>Contract address: {}</h1>
            </div>
        )
    }
}
export default App;
